// 删除DPE
// zhigang-li@siemens.com
SPC_DeleteDpe()
{

  mapping m1,m2,m3,m4;


  // 初始化，获得界面参数
  string comboBoxDepartmentSelectedText;
  string comboBoxSubSystemSelectedText;

  string dpe = txtDPE.text;
  string description = txtDesc.text;
  string HIGH_SPECIFICATION = txtHighSetpoint.text;
  string MIDDLE_SPECIFICATION = txtMiddleSetpoint.text;
  string LOW_SPECIFICATION = txtLowSetpoint.text;
  string trendMin = txtTrendMin.text;
  string trendMax = txtTrendMax.text;
  string department = comboBoxDepartment.selectedText();
  string subSystem = comboBoxSubSystem.selectedText();

  // DebugN(dpe, description, HIGH_SPECIFICATION,MIDDLE_SPECIFICATION,LOW_SPECIFICATION,trendMin, trendMax , department, subSystem);

  // 1. 是否找到 department？
  for(int i = 1; i &lt;= dynlen(dmJsonBuf); i++)
  {
    // 找课别
    if( dmJsonBuf[i][&quot;text&quot;] == department )
    {

      if(debugFlag) DebugN(&quot;Found department--&quot;, department);
      for(int j = 1; j &lt;= dynlen(dmJsonBuf[i][&quot;nodes&quot;]); j++)
      {
        // 找子系统
        if(dmJsonBuf[i][&quot;nodes&quot;][j][&quot;text&quot;] == subSystem)
        {

          if(debugFlag) DebugN(&quot;Found subSystem--&quot;, subSystem);
           // 是否存在dpe？
          for(int k = 1; k &lt;= dynlen( dmJsonBuf[i][&quot;nodes&quot;][j][&quot;nodes&quot;] ); k++)
          {
                // 相同dpe
                if( (dpe == dmJsonBuf[i][&quot;nodes&quot;][j][&quot;nodes&quot;][k][&quot;nodes&quot;][1][&quot;text&quot;] ) ||
                    (description == dmJsonBuf[i][&quot;nodes&quot;][j][&quot;nodes&quot;][k][&quot;text&quot;] ) )
                {
                   string uuid3 = dmJsonBuf[i][&quot;nodes&quot;][j][&quot;nodes&quot;][k][&quot;uuid&quot;];
                   string uuid4 = dmJsonBuf[i][&quot;nodes&quot;][j][&quot;nodes&quot;][k][&quot;nodes&quot;][1][&quot;uuid&quot;];

                   dynRemove(dmJsonBuf[i][&quot;nodes&quot;][j][&quot;nodes&quot;],k);

                   TREE1.setForeColor(uuid3, 0, &quot;red&quot;);
                   TREE1.setForeColor(uuid4, 0, &quot;red&quot;);
                   btSave.enabled = true;

                   return;


                   //;
               }
         }

       }

      }



    }


  }


}


// 修改DPE
// zhigang-li@siemens.com
SPC_ModifyDpe()
{

  mapping m1,m2,m3,m4;


  // 初始化，获得界面参数
  string comboBoxDepartmentSelectedText;
  string comboBoxSubSystemSelectedText;

  string dpe = txtDPE.text;
  string description = txtDesc.text;
  string HIGH_SPECIFICATION = txtHighSetpoint.text;
  string MIDDLE_SPECIFICATION = txtMiddleSetpoint.text;
  string LOW_SPECIFICATION = txtLowSetpoint.text;
  string trendMin = txtTrendMin.text;
  string trendMax = txtTrendMax.text;
  string department = comboBoxDepartment.selectedText();
  string subSystem = comboBoxSubSystem.selectedText();
  string sysId = txtSysId.text;
  string direction = comboBoxDirection.text;
  string sigma = comboSigma.text;
  string visibleMax = txtVisibleMax.text;
  string visibleMin = txtVisibleMin.text;
  string force_UCL = txtUCL.text;
  string force_LCL = txtLCL.text;
  string force_Middle = txtMiddle.text;
  string warmUp=txtWarmUp.text;
  string warmDown=txtWarmDown.text;

  if( (dpe == &quot;&quot;) ||
        (description == &quot;&quot;) ||
        (HIGH_SPECIFICATION == &quot;&quot;) ||
        (MIDDLE_SPECIFICATION == &quot;&quot;) ||
        (LOW_SPECIFICATION == &quot;&quot;) ||
        (trendMin == &quot;&quot;) ||
        (trendMax == &quot;&quot;) ||
        (department == &quot;&quot;) ||
        (subSystem == &quot;&quot;)  ||
        (force_UCL == &quot;&quot;)  ||
        (force_LCL == &quot;&quot;)  ||
        (force_Middle == &quot;&quot;)  ||
        (sigma == &quot;选择&quot;)   ||
        (visibleMax == &quot;&quot;)   ||
        (visibleMax == &quot;&quot;)    )
    {

     ChildPanelOnCentralModal(&quot;vision/messages/MessageInfo1.pnl&quot;, &quot;&quot;, makeDynString(&quot;$1:检查数据是否为空\n&quot;+dpe+&quot;\n无法保存!&quot;));
     return;
    }
  // DebugN(dpe, description, HIGH_SPECIFICATION,MIDDLE_SPECIFICATION,LOW_SPECIFICATION,trendMin, trendMax , department, subSystem);

  // 1. 是否找到 department？
  for(int i = 1; i &lt;= dynlen(dmJsonBuf); i++)
  {
    // 找课别
    if( dmJsonBuf[i][&quot;text&quot;] == department )
    {

      for(int j = 1; j &lt;= dynlen(dmJsonBuf[i][&quot;nodes&quot;]); j++)
      {
        // 找子系统
        if(dmJsonBuf[i][&quot;nodes&quot;][j][&quot;text&quot;] == subSystem)
        {

           // 是否存在dpe？
          for(int k = 1; k &lt;= dynlen( dmJsonBuf[i][&quot;nodes&quot;][j][&quot;nodes&quot;] ); k++)
          {
                // 相同dpe
                if( (dpe         == dmJsonBuf[i][&quot;nodes&quot;][j][&quot;nodes&quot;][k][&quot;nodes&quot;][1][&quot;text&quot;] )   )
                {


               string parentId = dmJsonBuf[i][&quot;nodes&quot;][j][&quot;uuid&quot;];

//            DebugN(&quot;相同dpe-- &quot;, department,subSystem, dpe,
//                    parentId,
//                   description, HIGH_SPECIFICATION,
//                   MIDDLE_SPECIFICATION,LOW_SPECIFICATION,
//                   trendMin, trendMax ,
//                   department, subSystem);


               mapping m4 = makeMapping(  &quot;text&quot;, dpe,
                                       &quot;selectable&quot;, false ,
                                       &quot;checkable&quot;, true,
                                       &quot;LOW_SPECIFICATION&quot;, LOW_SPECIFICATION,
                                       &quot;MIDDLE_SPECIFICATION&quot;, MIDDLE_SPECIFICATION,
                                       &quot;HIGH_SPECIFICATION&quot;,HIGH_SPECIFICATION,
                                       &quot;trendMin&quot;, trendMin,
                                       &quot;trendMax&quot;, trendMax,
                                       &quot;sysId&quot;, sysId,
                                       &quot;sigma&quot;,sigma,
                                       &quot;visibleMax&quot;,visibleMax,
                                       &quot;visibleMin&quot;,visibleMin,
                                       &quot;direction&quot;, direction,
                                       &quot;force_UCL&quot;, force_UCL,
                                       &quot;force_LCL&quot;, force_LCL,
                                       &quot;force_Middle&quot;, force_Middle,
                                       &quot;warmUp&quot;,warmUp,
                                       &quot;warmDown&quot;,warmDown,
                                       &quot;uuid&quot;,  dmJsonBuf[i][&quot;nodes&quot;][j][&quot;nodes&quot;][k][&quot;nodes&quot;][1][&quot;uuid&quot;],
                                       &quot;state&quot;, makeMapping(&quot;disabled&quot;,false ));

               dmJsonBuf[i][&quot;nodes&quot;][j][&quot;nodes&quot;][k][&quot;nodes&quot;][1] = m4;

               dmJsonBuf[i][&quot;nodes&quot;][j][&quot;nodes&quot;][k][&quot;text&quot;] = description;

               TREE1.setForeColor(dmJsonBuf[i][&quot;nodes&quot;][j][&quot;nodes&quot;][k][&quot;nodes&quot;][1][&quot;uuid&quot;], 0, &quot;green&quot;);
               TREE1.setForeColor(dmJsonBuf[i][&quot;nodes&quot;][j][&quot;nodes&quot;][k][&quot;uuid&quot;], 0, &quot;green&quot;);

               btSave.enabled = TRUE;
               return;


               //;
           }
         }

       }

      }



    }


  }


}

// 添加新DPE
// zhigang-li@siemens.com
SPC_AddNewDpe()
{


  mapping m1,m2,m3,m4;


  // 初始化，获得界面参数
  string comboBoxDepartmentSelectedText;
  string comboBoxSubSystemSelectedText;

  string dpe = txtDPE.text;
  string description = txtDesc.text;
  string HIGH_SPECIFICATION = txtHighSetpoint.text;
  string MIDDLE_SPECIFICATION = txtMiddleSetpoint.text;
  string LOW_SPECIFICATION = txtLowSetpoint.text;
  string trendMin = txtTrendMin.text;
  string trendMax = txtTrendMax.text;
  string department = comboBoxDepartment.selectedText();
  string subSystem = comboBoxSubSystem.selectedText();
  string sysId ;
  string direction = comboBoxDirection.text;
  string sigma = comboSigma.text;
  string visibleMax = txtVisibleMax.text;
  string visibleMin = txtVisibleMin.text;
  string force_UCL = txtUCL.text;
  string force_LCL = txtLCL.text;
  string force_Middle = txtMiddle.text;
  string warmUp=txtWarmUp.text;
  string warmDown=txtWarmDown.text;

  if( (dpe == &quot;&quot;) ||
      (description == &quot;&quot;) ||
      (HIGH_SPECIFICATION == &quot;&quot;) ||
      (MIDDLE_SPECIFICATION == &quot;&quot;) ||
      (LOW_SPECIFICATION == &quot;&quot;) ||
      (trendMin == &quot;&quot;) ||
      (force_UCL == &quot;&quot;)  ||
      (force_LCL == &quot;&quot;)  ||
      (force_Middle == &quot;&quot;)  ||
      (trendMax == &quot;&quot;) ||
      (department == &quot;&quot;) ||
      (subSystem == &quot;&quot;) ||
      (sigma == &quot;选择&quot;) ||
      (visibleMax == &quot;&quot;) ||
      (visibleMin == &quot;&quot;)  )
  {

   ChildPanelOnCentralModal(&quot;vision/messages/MessageInfo1.pnl&quot;, &quot;&quot;, makeDynString(&quot;$1:检查数据是否为空\n&quot;+dpe+&quot;\n无法添加!&quot;));
   return;
  }

  DebugN(dpe, description, HIGH_SPECIFICATION,MIDDLE_SPECIFICATION,LOW_SPECIFICATION,trendMin, trendMax , department, subSystem);

  // 1. 是否找到 department？
  for(int i = 1; i &lt;= dynlen(dmJsonBuf); i++)
  {
    // 找课别
    if( dmJsonBuf[i][&quot;text&quot;] == department )
    {

      sysId = dmJsonBuf[i][&quot;sysId&quot;];
      if(debugFlag) DebugN(&quot;found DEPARTMENT -- &quot;, department);
      for(int j = 1; j &lt;= dynlen(dmJsonBuf[i][&quot;nodes&quot;]); j++)
      {
        // 找子系统
        if(dmJsonBuf[i][&quot;nodes&quot;][j][&quot;text&quot;] == subSystem)
        {

          if(debugFlag) DebugN(&quot;found SYSTEM -- &quot;, subSystem);
           // 是否存在dpe？
          for(int k = 1; k &lt;= dynlen( dmJsonBuf[i][&quot;nodes&quot;][j][&quot;nodes&quot;] ); k++)
          {
            // 相同dpe
            if(  dpe == dmJsonBuf[i][&quot;nodes&quot;][j][&quot;nodes&quot;][k][&quot;nodes&quot;][1][&quot;text&quot;])

            {
              ChildPanelOnCentralModal(&quot;vision/messages/MessageInfo1.pnl&quot;, &quot;&quot;, makeDynString(&quot;$1:存在该测点\n&quot;+dpe+&quot;\n无法添加!&quot;));
              return  ;
            }

          }

           // 新dpe
           string parentId = dmJsonBuf[i][&quot;nodes&quot;][j][&quot;uuid&quot;];

           DebugN(&quot;新增dpe-- &quot;, department,subSystem, dpe,
                   parentId,
                  description, HIGH_SPECIFICATION,
                  MIDDLE_SPECIFICATION,LOW_SPECIFICATION,
                  trendMin, trendMax ,sysId,
                  department, subSystem);


           string uuid3, uuid4;
           uuid3 = createUuid();
           uuid4 = createUuid();

           dyn_mapping m4;
           dynInsertAt(m4, makeMapping(  &quot;text&quot;, dpe,
                                   &quot;selectable&quot;, false ,
                                   &quot;checkable&quot;, true,
                                   &quot;LOW_SPECIFICATION&quot;, LOW_SPECIFICATION,
                                   &quot;MIDDLE_SPECIFICATION&quot;, MIDDLE_SPECIFICATION,
                                   &quot;HIGH_SPECIFICATION&quot;,HIGH_SPECIFICATION,
                                   &quot;trendMin&quot;, trendMin,
                                   &quot;trendMax&quot;, trendMax,
                                   &quot;sysId&quot;, sysId,
                                   &quot;force_UCL&quot;, force_UCL,
                                   &quot;force_LCL&quot;, force_LCL,
                                   &quot;force_Middle&quot;, force_Middle,
                                   &quot;sigma&quot;, sigma,
                                   &quot;visibleMax&quot;, visibleMax,
                                   &quot;visibleMin&quot;, visibleMin,
                                   &quot;direction&quot;, direction,
                                   &quot;warmUp&quot;,warmUp,
                                   &quot;warmDown&quot;,warmDown,
                                   &quot;uuid&quot;,  uuid4,
                                   &quot;state&quot;, makeMapping(&quot;disabled&quot;, false )), 1);

           dynInsertAt(dmJsonBuf[i][&quot;nodes&quot;][j][&quot;nodes&quot;], makeMapping(  &quot;text&quot;, description,
                                           &quot;selectable&quot;, false,
                                           &quot;checkable&quot;, false,
                                           &quot;uuid&quot;,  uuid3 ,
                                           &quot;nodes&quot;, m4 ), 1);

           // 显示在TREE中
            TREE1.appendItemNC(  parentId ,  uuid3 ,  description );
            TREE1.appendItemNC(  uuid3 ,  uuid4 , dpe );

            TREE1.setText(uuid4, mColumnNameCN[&quot;规格下限（LSL）&quot;], LOW_SPECIFICATION);
            TREE1.setText(uuid4, mColumnNameCN[&quot;规格中线&quot;], MIDDLE_SPECIFICATION);
            TREE1.setText(uuid4, mColumnNameCN[&quot;规格上限（USL）&quot;], HIGH_SPECIFICATION);
            TREE1.setText(uuid4, mColumnNameCN[&quot;剔除下限&quot;], trendMin);
            TREE1.setText(uuid4, mColumnNameCN[&quot;剔除上限&quot;], trendMax );
            TREE1.setText(uuid4, mColumnNameCN[&quot;系统号&quot;], sysId );
            TREE1.setText(uuid4, mColumnNameCN[&quot;方向&quot;], direction );
            TREE1.setText(uuid4, mColumnNameCN[&quot;sigma&quot;], sigma );
            TREE1.setText(uuid4, mColumnNameCN[&quot;视界上限&quot;], visibleMax );
            TREE1.setText(uuid4, mColumnNameCN[&quot;视界下限&quot;], visibleMin );
            TREE1.setText(uuid4, mColumnNameCN[&quot;管制上限&quot;], force_UCL );
            TREE1.setText(uuid4, mColumnNameCN[&quot;视界中线&quot;], force_Middle );
            TREE1.setText(uuid4, mColumnNameCN[&quot;管制下限&quot;], force_LCL );
            TREE1.setText(uuid4, mColumnNameCN[&quot;Warm上限&quot;], warmUp );
            TREE1.setText(uuid4, mColumnNameCN[&quot;Warm下限&quot;], warmDown );
            TREE1.setForeColor(uuid3, 0, &quot;green&quot;);
            TREE1.setForeColor(uuid4, 0, &quot;green&quot;);

            newAddedId4 = uuid4;
            newAddedId3 = uuid3;


            btSave.enabled = true;
            ChildPanelOnCentralModal(&quot;vision/messages/MessageInfo1.pnl&quot;, &quot;&quot;, makeDynString(&quot;$1:添加OK\n&quot;+dpe+&quot;\n请保存!&quot;));






           //;


       }

      }



    }


  }


}


// 读取JSON格式的数据文件
// zhigang-li@siemens.com
dyn_mapping SPC_GetJsonData()
{
  string path = getPath(DATA_REL_PATH) + &quot;Reportv2/config/menuJsonSPC.txt&quot;;

  string str;

  fileToString( path, str);

  dyn_mapping dm = jsonDecode(str);

  return dm;

}






// 初始化TREE控件
// zhigang-li@siemens.com
SPC_UpdateTree()
{



  dyn_mapping m1, m2, m3, m4;
  string parentId;
  string uuid;

  dyn_mapping m1 = dmJsonBuf ;

  for(int i = 1; i &lt;= dynlen(m1); i++)
  {
    // 1. 课别
    string parentId1 = &quot;&quot;;
    string uuid1 = m1[i][&quot;uuid&quot;];
    string text1 = m1[i][&quot;text&quot;];
    m2 = m1[i][&quot;nodes&quot;];

    TREE1.appendItemNC(  parentId1 ,  uuid1 ,  text1 );

          // 2. 子系统
          for(int j = 1; j &lt;= dynlen(m2); j++)
          {
            string parentId2 = uuid1 ;
            string uuid2 = m2[j][&quot;uuid&quot;];
            string text2 = m2[j][&quot;text&quot;];
            m3 = m2[j][&quot;nodes&quot;];

            TREE1.appendItemNC(  parentId2 ,  uuid2 ,  text2 );

                 // 3. 侦测点
                for(int k = 1; k &lt;= dynlen(m3); k++)
                {
                  string parentId3 = uuid2 ;
                  string uuid3 = m3[k][&quot;uuid&quot;];
                  string text3 = m3[k][&quot;text&quot;];
                  m4 = m3[k][&quot;nodes&quot;];

                  TREE1.appendItemNC(  parentId3 ,  uuid3 ,  text3 );

                        // 4. DPE
                    for(int l = 1; l &lt;= dynlen(m4); l++)
                    {
                      string parentId4 = uuid3 ;
                      string uuid4 = m4[l][&quot;uuid&quot;];
                      string text4 = m4[l][&quot;text&quot;];

                      TREE1.appendItemNC(  parentId4 ,  uuid4 ,  text4 );

                      TREE1.setText(uuid4, mColumnNameCN[&quot;规格下限（LSL）&quot;], m4[l][&quot;LOW_SPECIFICATION&quot;]);
                      TREE1.setText(uuid4, mColumnNameCN[&quot;规格中线&quot;], m4[l][&quot;MIDDLE_SPECIFICATION&quot;]);
                      TREE1.setText(uuid4, mColumnNameCN[&quot;规格上限（USL）&quot;], m4[l][&quot;HIGH_SPECIFICATION&quot;]);
                      TREE1.setText(uuid4, mColumnNameCN[&quot;剔除下限&quot;], m4[l][&quot;trendMin&quot;]);
                      TREE1.setText(uuid4, mColumnNameCN[&quot;剔除上限&quot;], m4[l][&quot;trendMax&quot;]);
                      TREE1.setText(uuid4, mColumnNameCN[&quot;系统号&quot;], m4[l][&quot;sysId&quot;]);
                      if(mappingHasKey(m4[l], &quot;direction&quot; ) )
                      {

                        TREE1.setText(uuid4, mColumnNameCN[&quot;方向&quot;], m4[l][&quot;direction&quot;]);
                      }
                      else
                      {
                        TREE1.setText(uuid4, mColumnNameCN[&quot;方向&quot;], &quot;双边&quot; );
                      }

                      if(mappingHasKey(m4[l], &quot;sigma&quot; ) )
                      {

                        TREE1.setText(uuid4, mColumnNameCN[&quot;sigma&quot;], m4[l][&quot;sigma&quot;] );
                      }
                      else
                      {
                        TREE1.setText(uuid4, mColumnNameCN[&quot;sigma&quot;], &quot;3σ&quot; );
                      }


                      if(mappingHasKey(m4[l], &quot;visibleMax&quot; ) )
                      {

                        TREE1.setText(uuid4, mColumnNameCN[&quot;视界上限&quot;], m4[l][&quot;visibleMax&quot;]);
                      }
                      else
                      {
                        TREE1.setText(uuid4, mColumnNameCN[&quot;视界上限&quot;], (float)m4[l][&quot;trendMax&quot;]+1 );
                      }


                      if(mappingHasKey(m4[l], &quot;visibleMin&quot; ) )
                      {
                        TREE1.setText(uuid4, mColumnNameCN[&quot;视界下限&quot;], m4[l][&quot;visibleMin&quot;]);
                      }
                      else
                      {
                        TREE1.setText(uuid4, mColumnNameCN[&quot;视界下限&quot;], (float)m4[l][&quot;trendMin&quot;]-1 );
                      }


                      if(mappingHasKey(m4[l], &quot;force_UCL&quot; ) )
                      {
                        TREE1.setText(uuid4, mColumnNameCN[&quot;管制上限&quot;], m4[l][&quot;force_UCL&quot;]);
                      }
                      else
                      {
                        TREE1.setText(uuid4, mColumnNameCN[&quot;管制上限&quot;],  0 );
                      }

                      if(mappingHasKey(m4[l], &quot;force_LCL&quot; ) )
                      {
                        TREE1.setText(uuid4, mColumnNameCN[&quot;管制下限&quot;], m4[l][&quot;force_LCL&quot;]);
                      }
                      else
                      {
                        TREE1.setText(uuid4, mColumnNameCN[&quot;管制下限&quot;],  0 );
                      }


                      if(mappingHasKey(m4[l], &quot;force_Middle&quot; ) )
                      {
                        TREE1.setText(uuid4, mColumnNameCN[&quot;管制中线&quot;], m4[l][&quot;force_Middle&quot;]);
                      }
                      else
                      {
                        TREE1.setText(uuid4, mColumnNameCN[&quot;管制中线&quot;],  0 );
                      }

                      if(mappingHasKey(m4[l], &quot;warmUp&quot; ) )
                      {
                        TREE1.setText(uuid4, mColumnNameCN[&quot;Warm上限&quot;], m4[l][&quot;warmUp&quot;]);
                      }
                      else
                      {
                        TREE1.setText(uuid4, mColumnNameCN[&quot;Warm上限&quot;],  0 );
                      }

                      if(mappingHasKey(m4[l], &quot;warmDown&quot; ) )
                      {
                        TREE1.setText(uuid4, mColumnNameCN[&quot;Warm下限&quot;], m4[l][&quot;warmDown&quot;]);
                      }
                      else
                      {
                        TREE1.setText(uuid4, mColumnNameCN[&quot;Warm下限&quot;],  0 );
                      }
                    }

                }
          }

  }




}



// 初始化TREE控件
// zhigang-li@siemens.com
SPC_InitTree()
{

  this.addColumn(&quot;选择&quot;);
  this.addColumn(&quot;规格下限（LSL）&quot;);
  this.addColumn(&quot;规格中线&quot;);
  this.addColumn(&quot;规格上限（USL）&quot;);
  this.addColumn(&quot;剔除下限&quot;);
  this.addColumn(&quot;剔除上限&quot;);
  this.addColumn(&quot;系统号&quot;);
  this.addColumn(&quot;方向&quot;);
  this.addColumn(&quot;sigma&quot;);
  this.addColumn(&quot;视界上限&quot;);
  this.addColumn(&quot;视界下限&quot;);
  this.addColumn(&quot;管制上限(UCL)&quot;);
  this.addColumn(&quot;管制中线&quot;);
  this.addColumn(&quot;管制下限(LCL)&quot;);
  this.addColumn(&quot;Warm上限&quot;);
  this.addColumn(&quot;Warm下限&quot;);



}

main()
{

  mColumnNameCN[&quot;规格下限（LSL）&quot;] = 1;
  mColumnNameCN[&quot;规格中线&quot;] = 2;
  mColumnNameCN[&quot;规格上限（USL）&quot;] = 3;
  mColumnNameCN[&quot;剔除下限&quot;] = 4;
  mColumnNameCN[&quot;剔除上限&quot;] = 5;
  mColumnNameCN[&quot;系统号&quot;] = 6;
  mColumnNameCN[&quot;方向&quot;] = 7;
  mColumnNameCN[&quot;sigma&quot;] =8;
