main()
{


  comboBoxSubSystem.deleteAllItems();


  dyn_mapping dm = SPC_GetJsonData();

  for(int i = 1; i &lt;= dynlen(dm); i++)
  {
    string department =  dm[i][&quot;text&quot;];
    if(department ==  comboBoxDepartment.selectedText )
    {
      for(int j = 1; j &lt;= dynlen( dm[i][&quot;nodes&quot;] ); j++)
      {
            comboBoxSubSystem.appendItem(  dm[i][&quot;nodes&quot;][j][&quot;text&quot;] );
      }

    }

  }
}

